      const commandName = "numberedList";
      editor.ui.componentFactory.add(commandName, getDropdownViewCreator({
        editor,
        normalizedConfig,
        parentCommandName: commandName,
        buttonLabel,
        buttonIcon: icons.numberedList,
        styleGridAriaLabel,
        styleDefinitions
      }));
